INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690', 2, 'Usiku unatambuliwa katika kijiji cha Opio', 'assets/topic/asb/1130.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_3', 3, NULL, 'assets/topic/asb/1131.png', 'Hapo zamani hakukuwa na majira ya usiku katika kijiji cha kina Opio. Watu walilala wakati wowote walipochoka. Walifanya kazi zao walipoamka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_4', 3, NULL, 'assets/topic/asb/1132.png', 'Siku moja, Opio alienda na mbwa wake kuwinda. Baada ya muda mfupi, mbwa walianza kumfukuza paa. Opio aliwafuata akakimbia kwa muda wa saa nyingi. Alipochoka alipumzika halafu baadaye alianza kukimbia tena. Lakini, hakuwaona mbwa wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_5', 3, NULL, 'assets/topic/asb/1133.png', 'Opio alijipata katika kijiji tofauti. Alimwambia mwenye nyumba, "Nimechoka. Nahitaji kupumzika." Mtu yule alimjibu, "Unakaribishwa kupumzika hapa. Je, ungependa kunywa pombe kidogo?" Opio alikuwa na kiu. Aliinywa pombe kidogo kisha akalala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_6', 3, NULL, 'assets/topic/asb/1134.png', 'Opio alipoamka, hakuweza kuona. Aliyafumba na kuyafumbua macho yake mara nyingi. Mwishowe,  alimwambia mhisani wake, "Ulinipa kinywaji kibaya. Siwezi tena kuona sawasawa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_7', 3, NULL, 'assets/topic/asb/1135.png', 'Mwenye nyumba alimwambia, "Hukunywa kinywaji kibaya na macho yako ni sawasawa. Sasa hivi ni usiku. Kwani hujui usiku? Siku ikiisha, usiku hufuata." Opio aliuliza maswali mengi kuhusu usiku akapendezwa sana nao. Alikaa hapo siku nyingine  ili auone usiku ukiingia tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_8', 3, NULL, 'assets/topic/asb/1136.png', 'Asubuhi iliyofuata, yule mtu alimweleza Opio, "Giza litakapoanza kuingia, anza kutembea ukienda kijijini kwenu. Ukitembea huku ukiangalia mbele usiku utakufuata. Ukiangalia nyuma usiku utapotea."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_9', 3, NULL, 'assets/topic/asb/1130.png', 'Opio alifanya alivyoambiwa. Usiku huo alianza kutembea akielekea kijijini kwao. Alihisi giza likimfuata nyuma. Alikuwa na haja ya kuangalia nyuma lakini alijizuia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_10', 3, NULL, 'assets/topic/asb/1137.png', 'Alipofika kijijini kwao, wanakijiji waliogopa. Walimwuliza, "Huu ni ugonjwa gani uliotuletea? Ni nini hiki cheusi kinachokufuata?" Opio akawaeleza, "Ndugu zangu, kitu hiki cheusi kinachonifuata kinaitwa usiku. Mnavyoogopa ndivyo nilivyoogopa pia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_11', 3, NULL, 'assets/topic/asb/1138.png', 'Opio aliendelea kuwaeleza, "Ninyi vilevile mtaupenda usiku. Mwangaza ukija tutaweza kufanya kazi. Na usiku ukiingia tutapumzika."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8690_12', 3, NULL, 'assets/topic/asb/1139.png', 'Na hivyo ndivyo usiku ulivyotambuliwa katika kijiji cha kina Opio.', NULL);
