INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22067_6', 3, NULL, 'assets/topic/asb/10279.png', 'Seremala naye akasema kwamba yeye ndiye aliyekuwa na kazi muhimu zaidi.
