INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611', 2, 'Vayu, upepo', 'assets/topic/asb/2442.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_3', 3, NULL, 'assets/topic/asb/2443.png', 'Ninapomaliza kuoga kwa maji moto, mwili wangu huwa majimaji na kuhisi baridi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_4', 3, NULL, 'assets/topic/asb/2444.png', 'Kwenye kikombe kuna maziwa moto sana. Baada ya muda, maziwa yanapoa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_5', 3, NULL, 'assets/topic/asb/2445.png', 'Pazia za dirisha zinapapatika polepole zikinigusa uso.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_6', 3, NULL, 'assets/topic/asb/2448.png', 'Ninaona radi. Mawingu meusi yananikaribia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_7', 3, NULL, 'assets/topic/asb/2449.png', 'Matawi yanayumbayumba na majani yanapepea. Maua yanadondoka kutoka mtini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_8', 3, NULL, 'assets/topic/asb/2450.png', 'Tunapocheza nje, tunanukia pipi anazotayarisha mama.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_9', 3, NULL, 'assets/topic/asb/2451.png', 'Bilauri iliyokuwa dirisahani, imeanguka na kuvunjika. Namshukuru Mungu haikuniangukia. Ninajua aliyesababisha ukatili huu. Bila shaka ni Vayu, upepo!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_10', 3, NULL, 'assets/topic/asb/2452.png', 'Firimbi imepulizwa. Gari la moshi linafika kituoni. Siwezi kuliona, lakini nausikia mngurumo wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2611_11', 3, NULL, 'assets/topic/asb/2453.png', 'Haonekani. Hasikiki. Hufanya kazi zote kisirisiri. Anaweza kuwa ni nani?
