INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440', 2, 'Viatu  vyangu vya kwanza', 'assets/topic/asb/9500.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_3', 3, NULL, 'assets/topic/asb/9494.png', 'Sikuwa nimewahi kuvaa viatu. Nilipowaona watoto waliovaa viatu, niliwatazama kwa hamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_4', 3, NULL, 'assets/topic/asb/9492.png', 'Mamangu alinifariji akisema, "Utakuwa na jozi nyingi baadaye, subiri tu utaona!" Wakati mwingine sikumwamini. "Baadaye, itakuwa lini?" Nilimwuliza siku moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_5', 3, NULL, 'assets/topic/asb/9493.png', 'Kisha ulikuwa msimu wa Krismasi. Kila mtu alijishughulisha kwenda sokoni na kurejea na vitu vipya. "Mama, je, nasi tutaenda sokoni?" Nilimwuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_6', 3, NULL, 'assets/topic/asb/9496.png', 'Siku kabla ya Krismasi, mamangu aliniamsha mapema kuliko ilivyokuwa kawaida. Alinieleza nilichukue lile kapu kubwa ambalo alilibeba kila alipokwenda sokoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_7', 3, NULL, 'assets/topic/asb/9495.png', 'Sokoni palikuwa na wazazi wengi walioandamana na watoto wao. Walinunua nguo mpya na kiasi kikubwa cha vyakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_8', 3, NULL, 'assets/topic/asb/9497.png', 'Tulienda moja kwa moja hadi sehemu ya viatu aina tofauti. Nilishangaa kwa kuona mstari baada ya mwingine wa viatu vilivyopangwa kwa unadhifu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_9', 3, NULL, 'assets/topic/asb/9499.png', 'Baada ya kupima baadhi ya jozi nyingi, nilichukua jozi ya viatu vya michezo. Sikuweza kulala usiku huo. Nilisisimkwa sana nilipowaza jinsi ningevivaa viatu vyangu vipya na kujivunia kila mtoto kijijini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_10', 3, NULL, 'assets/topic/asb/10444.png', 'Muda mfupi baadaye, niliamka kitandani na kuvijaribu tena viatu vyangu. Nilitembea polepole chumbani kisha nikavirejesha ndani ya sanduku. Nilijaribu kulala lakini sikupata usingizi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_11', 3, NULL, 'assets/topic/asb/9498.png', 'Niliamka na kuvijaribu tena kwa mara ya pili. Nilitembea kwa maringo chumbani kisha nikavirejesha sandukuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_12', 3, NULL, 'assets/topic/asb/9502.png', 'Nilijaribu tena kulala nikinuia kupata usingizi. Lakini muda mfupi baadaye, niliamka kwa mara ya tatu. Nilivivaa vyatu nikaruka ruka kidogo chumbani. Nilihisi usingizi nikarudi kitandani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_13', 3, NULL, 'assets/topic/asb/9501.png', 'Asubuhi yake, niliamshwa na mamangu. "Hiki ni nini ninachoona?" Aliniuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10440_14', 3, NULL, 'assets/topic/asb/9500.png', 'Nilikuwa nimevaa viatu vyangu vipya kitandani!', NULL);
