INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135', 2, 'Vitu nivijuavyo', 'assets/topic/asb/2550.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_3', 3, NULL, 'assets/topic/asb/2551.png', 'Hii ni manyatta. Ni kwetu nyumbani. Iko katika Kaunti ya Turkana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_4', 3, NULL, 'assets/topic/asb/2552.png', 'Huyu ni ngamia. Katika Kaunti ya Turkana,  watu wanafuga wanyama tofauti. Lakini ngamia ni muhimu mno.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_5', 3, NULL, 'assets/topic/asb/2553.png', 'Hawa ni walimu wangu. Ninapenda kwenda shuleni. Shule yangu inaitwa Shule ya Msingi ya Lolupe. Nampenda sana mwalimu wangu wa Sanaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_6', 3, NULL, 'assets/topic/asb/2554.png', 'Hii ni meli kubwa. Meli zinatumika kusafirisha watu na mizigo majini. Nikienda Mombasa, nitaweza kuona meli.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_7', 3, NULL, 'assets/topic/asb/2550.png', 'Hiki ni kitanda. Ninapenda kitanda babangu alinitengezea. Nakitumia na ndugu yangu mdogo. Ni kizuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_8', 3, NULL, 'assets/topic/asb/2555.png', 'Huyu ni jogoo. Tuna jogoo wawili nyumbani. Tunao pia kuku wengi. Kuku hutaga mayai mengi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_9', 3, NULL, 'assets/topic/asb/2556.png', 'Huyu ni panya. Panya ni wanyama hatari. Hula chakula. Huharibu nguo pia. Huweza kusababisha ugonjwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_10', 3, NULL, 'assets/topic/asb/2557.png', 'Huyu ni mbwa. Mbwa hufugwa. Mbwa wetu anaitwa Daa. Hubweka sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_11', 3, NULL, 'assets/topic/asb/2558.png', 'Huu ni mwavuli. Wazazi wangu wana mwavuli mkubwa. Una rangi ya kisambarau, kimanjano na kijani kibichi. Tunautumia inaponyesha ili tusinyeshewe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3135_12', 3, NULL, 'assets/topic/asb/2559.png', 'Niko darasa la pili na ninajua vitu vingi. Nataka kujua vingine zaidi. Nisivyojua, nawauliza wazazi wangu au mwalimu wangu.', NULL);
