INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533', 2, 'Wanamuziki stadi', 'assets/topic/asb/25963.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_3', 3, NULL, 'assets/topic/asb/25955.png', 'Bul, Fumbo, Teke, Tongoli, Milege, Sos na Gwara walikuwa marafiki wakubwa. Pia waliimba muziki mzuri. Waliishi karibu na milima mashariki mwa Uganda. Wote walikuwa wanamuziki stadi. Kila mtu alifurahi kusikiliza nyimbo zao na kucheza kufuata mdundo wao! Hawa wanamuziki stadi walitaka kumchagua kiongozi wao. Aliyetambuliwa kwa umaarufu wake zaidi ya wote, ndiye angekuwa mfalme wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_4', 3, NULL, 'assets/topic/asb/25956.png', 'Ili kuchaguliwa kama mfalme, kila mwanamuziki alitakiwa kudhihirisha kwamba anaweza kusikika na walio karibu na vilevile walio mbali. Tongoli aliongea kwanza, "Mimi ni mfalme. Ninaimba sauti ya juu na
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_5', 3, NULL, 'assets/topic/asb/25957.png', '"Ulivyosema ni ukweli Tongoli," Fumbo alikubali. "Lakini, wewe hunishindi mimi. Sauti yako ni laini sana. Mimi nina sauti kubwa iliyo wazi na inayofika karibu na mbali." Fumbo aliendelea, "Watu hucheza muziki wangu mpaka karibu wavunjike migongo! Iwe wakati wa furaha au wa majonzi, mimi huwafanya watu wacheze! Watoto hupenda kuiga midundo yangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_6', 3, NULL, 'assets/topic/asb/25958.png', '"Nyote wawili ni viongozi," Milege alikubali kisha akaendelea, "Kazi yangu ni rahisi. Wachezaji hunifunga vifundoni. Wanapoitikisa miguu yao kwa mdundo, mimi hutoa muziki wa kupendeza. Ninatoa mchanganyiko wa sauti za juu na zinazokwaruza. Je, mimi si kiongozi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_7', 3, NULL, 'assets/topic/asb/25959.png', '"Hiyo ni sawa!" Bul alisema. "Mimi nimetengenezwa
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_8', 3, NULL, 'assets/topic/asb/25960.png', '"Bul, wewe hutenda mambo makuu!" Gwara alitofautiana naye. "Wewe huwaita watu kwenye mikutano, ibada, na kazi za kijamii na pia hutangaza vifo na kuwatahadharisha watu kunapokuwa na hatari. Unasikika wakati wa sherehe na kuwafanya watu wacheze kwa furaha na kwa huzuni. Kulingana nami, wewe ni mkubwa zaidi." Gwara alitoa mlio mkubwa na kusema, "Mimi nina sauti kubwa tena ninawavutia watu. Hata hivyo, mimi si bora kumliko Bul. Ninapendekeza yeye awe mfalme."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_9', 3, NULL, 'assets/topic/asb/25961.png', 'Wanamuziki hao stadi walitazamana kwa mshangao wakabaki kimya. Kisha Tongoli akasema, "Bul ni mtu wa kawaida sana, hawezi kuwa mfalme. Mimi ndiye mkubwa zaidi kwa sababu ninacheza kuanza kwa sauti ya chini hadi ya juu zaidi bila tatizo lolote." Sos ambaye alikuwa kimya wakati huo wote, sasa aliongea, "Marafiki, mimi nimekuwa nikiimba nanyi nyote. Nyote ni wanamuziki stadi. Lakini, ni bora tumchague Bul kuwa mflame wetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22533_10', 3, NULL, 'assets/topic/asb/25963.png', 'Sos aliendelea, "Mtu asiyesikia muziki unaoimbwa na Bul ni kama gogo lililokufa! Bul huwavutia watu kwake na kuwaliwaza kwa muziki wakati wa huzuni na kuwaburudisha wakati wa furaha." Mwishowe, wanamuziki hao stadi walimchagua Bul kuwa mfalme wao. Tangu wakati huo, Bul amekuwa mfalme wa muziki. Hiyo ndiyo sababu Bul yuko katika kila nyumba ya jamii inayoishi milimani mashariki mwa Uganda.', NULL);
