INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384', 2, 'Wanyama hushukuru', 'assets/topic/asb/17299.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_3', 3, NULL, 'assets/topic/asb/17289.png', 'Hapo zamani za kale, Binadamu, Simba, Punda,  Nyoka, Tai, na Mbwa walikuwa wamekwenda vitani pamoja. Walisafiri siku nzima hadi jua lilipotua. "Tutalala wapi?" Punda aliuliza. "Tazameni, ninaona moshi ukitoka katika nyumba ile. Tunaweza kulala huko," Nyoka alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_4', 3, NULL, 'assets/topic/asb/17290.png', 'Walipobisha, mtu na mkewe walitoka nje. Nyoka aliuliza, "Tunaweza kulala kwenu?" Waliwajibu, "Tunawakaribisha mlale, lakini, hatuna chakula cha kuwapa." Mbwa alisema, "Tulimwona ng''ombe wenu." Mume alijibu, "Huyo ng''ombe pekee ndiye tuliye naye. Tukimchinja, tutabaki bila chochote." Binadamu na wanyama walisema, "Tutawalipa. Tafadhali, tuchinjie ng''ombe wenu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_5', 3, NULL, 'assets/topic/asb/17291.png', 'Simba alisema, "Naomba damu. Nitakupa zawadi siku moja." "Nipe mifupa. Nitakuletea zawadi pia." Mbwa alisema." Binadamu alisema, "Nipe maziwa. Nitakupa zawadi maalum." Nyoka akasema, "Nipe mafuta. Hutajuta." "Naomba vipande vya sarara. Nitakupa zawadi pia," alisema Tai. Punda naye alisema, "Mimi nipe nyasi kutoka paa la nyumba yako. Siku moja utanishukuru."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_6', 3, NULL, 'assets/topic/asb/17292.png', 'Mtu na mkewe walimchinja ng''ombe wao wa pekee. Wakampa Simba damu, Mbwa akapewa mifupa, Binadamu akapewa maziwa, Nyoka akapewa mafuta, na Tai akapewa vipande vya sarara. Punda naye akapewa nyasi kutoka paa la nyumba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_7', 3, NULL, 'assets/topic/asb/17293.png', 'Wageni wale walilala vizuri usiku ule. Asubuhi kulipokucha wakaenda zao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_8', 3, NULL, 'assets/topic/asb/17294.png', 'Baada ya wiki nyingi, mtu huyo na mkewe walimsikia Simba akinguruma karibu na nyumba yao. Walipotoka nje, hawakumpata Simba, lakini, kulikuwa na pembe ya tembo iliyolazwa nyasini. "Pembe hii ni ya thamani. Ipeleke ndani. Lazima tuwe waangalifu wezi wasije kuiiba." Mume alimwambia mkewe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_9', 3, NULL, 'assets/topic/asb/17295.png', 'Ghafla, yule mtu na mkewe wakatazama nyuma yao. Walimwona Mbwa amelala karibu na mlango wa nyumba yao. Alisema, "Msiwe na wasiwasi. Nitawazuia wezi wasiingie nyumbani kwenu. Nakumbuka mlinisaidia na sasa wacha niwasaidie pia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_10', 3, NULL, 'assets/topic/asb/17296.png', 'Yule mtu alikuwa na kaka. Kakake huyo alikuwa tajiri, lakini, alikuwa mwovu na watu wote walimchukia. Alipopata habari kuhusu pembe ya tembo, aliwaza, "Ninaitaka pembe ile. Nitamuua kakangu halafu niichukue."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_11', 3, NULL, 'assets/topic/asb/17297.png', 'Usiku wa manane, alikwenda nyumbani kwa kakake. Nyoka mkubwa alikuwa amelala karibu na mlango wa nyumba ya mtu yule na mkewe. Nyoka huyo alimuuma kaka mwovu akaaga dunia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_12', 3, NULL, 'assets/topic/asb/17298.png', 'Katika nchi ile, mtu alipokufa, mali yake ilirithiwa na kakake. Kwa sababu hiyo, ng''ombe na kondoo wa kaka mwovu walirithiwa na kakake aliyekuwa hai. Yule mtu alimwambia mkewe, "Simba, Mbwa, na Nyoka wametulipa vizuri. Tai, Punda, na Binadamu watafanya nini?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_13', 3, NULL, 'assets/topic/asb/17299.png', 'Siku moja,  Tai alipokuwa akiruka juu angani, aliwaona wazee wakitembea pamoja kando ya barabara. Mikononi mwao walikuwa na kitu fulani kilichong''aa sana. "Nadhani wana pesa na vito. Inaonekana wanampelekea mume zawadi kutoka kwa mkewe au kutoka kwa mume kwenda kwa mkewe ili kumaliza ugomvi. Mwishowe,  watasameheana tu." Tai aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_14', 3, NULL, 'assets/topic/asb/17300.png', 'Tai aliruka chini akazichukua zile zawadi kutoka mikononi mwa wazee. Kisha aliziangusha zawadi hizo nje ya nyumba ya yule mtu na mkewe waliomsaidia mbeleni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_15', 3, NULL, 'assets/topic/asb/17301.png', 'Siku hiyo, Punda alikuwa katika msitu uliokuwa karibu. Mgongoni, alikuwa amebeba mfuko mzito uliojaa pesa. Bwana wake alimpiga akisema, "Enda kasi au nikuue! Wewe mnyama mpumbavu na mvivu!" Punda huyo aliwaza jinsi angemwadhibu bwana wake mwovu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_16', 3, NULL, 'assets/topic/asb/17302.png', 'Punda alitoroka akaenda nyumbani kwa yule mtu na mkewe akwaambia, "Chukueni pesa hizi. Ni zawadi yenu." Yule mtu na mkewe walitajirika kutoka siku ile.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_17', 3, NULL, 'assets/topic/asb/17303.png', 'Mtu yule alisema, "Rafiki zetu, wanyama,  wamekuwa wema sana kwetu. Ilikuwa vyema kwamba tulimchinja ng''ombe wetu wa pekee tukawapa." Mkewe alimjibu, "Lakini, Binadamu hajatupa zawadi yoyote. Hakika, wanyama wametufanyia mambo mengi. Ninajua Binadamu atatufanyia mambo mazuri zaidi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19384_18', 3, NULL, 'assets/topic/asb/17304.png', 'Walingoja, wakangoja. Wiki nyingi zikapita, kisha miezi na miaka, lakini Binadamu hakurudi. Wanyama wote walitimiza ahadi zao. Binadamu alisahau.', NULL);
