INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21130_9', 3, NULL, 'assets/topic/asb/13910.png', 'Maguru aliwapatia wanyama na ndege miguu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21130_10', 3, NULL, 'assets/topic/asb/13911.png', 'Baadhi ya wanyama walicheza. Wengine walianguka chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21130_11', 3, NULL, 'assets/topic/asb/13912.png', 'Walisema, “Hatutatambaa tena.”', NULL);
