INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21130_15', 3, NULL, 'assets/topic/asb/13916.png', 'Baadaye, Nyoka alifika. Alimsihi Maguru, “Tafadhali, nipe miguu.”', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21130_16', 3, NULL, 'assets/topic/asb/13917.png', 'Nyoka alimwambia Maguru, “Nililala kupita kiasi.”', NULL);
