INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23407_6', 3, NULL, 'assets/topic/asb/8520.png', 'Yule jogoo ali mkuta paka alie kuwa akitoroka kujiondoaa kwa wakurugenzi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23407_7', 3, NULL, 'assets/topic/asb/8521.png', 'Jogoo na paka wali mkuta mbwa ambaee pia alikua akitoroka.', NULL);
