INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_3', 3, NULL, 'assets/topic/asb/1296.png', 'Hapo zamani za kale, kulikuwa na familia moja iliyoishi kwa amani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_4', 3, NULL, 'assets/topic/asb/1297.png', 'Heri, Amani na Musa waliwasaidia wazazi nyumbani na shambani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_5', 3, NULL, 'assets/topic/asb/1298.png', 'Wazazi wao hawakuwaruhusu waende karibu na moto, wala kukaa juani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_6', 3, NULL, 'assets/topic/asb/1299.png', 'Heri, Amani na Musa walifanya kazi zao zote usiku.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_7', 3, NULL, NULL, 'Hii ni kwa sababu watoto hao waliumbwa kwa nta.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_8', 3, NULL, 'assets/topic/asb/1300.png', 'Musa alitamani kwenda nje wakati wa mchana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_9', 3, NULL, 'assets/topic/asb/1301.png', 'Siku moja tamaa ilimzidi. Nduguze, Heri na Amani,  walimwonya asiende juani. Musa hakuwasikiza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_10', 3, NULL, 'assets/topic/asb/1302.png', 'Joto lilipokuwa kali, Musa aliyeyuka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_11', 3, NULL, 'assets/topic/asb/1303.png', 'Heri na Amani walihuzunika walipomwona Musa akiyeyuka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2505_12', 3, NULL, 'assets/topic/asb/1304.png', 'Heri na Amani waliichukua nta iliyoyeyuka wakamtengeneza ndege.', NULL);
