INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746', 2, 'Watu waliosahau', 'assets/topic/asb/27234.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_3', 3, NULL, 'assets/topic/asb/27230.png', 'Hapo zamani za kale, kulikuwa na kijiji kilichoitwa Raha. Watu wote walikuwa vijana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_4', 3, NULL, 'assets/topic/asb/27231.png', 'Vijana wa kike na wa kiume kijijini Raha walicheza na kufurahi. Walikula walichopenda na kunywa chochote kilichowalevya. Walifurahi mchana kutwa na usiku kucha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_5', 3, NULL, 'assets/topic/asb/27232.png', 'Pia walisahau chochote kilichotendeka siku iliyopita.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_6', 3, NULL, 'assets/topic/asb/27233.png', 'Hapakuwa na muziki wa kucheza. Hata hawangeweza kucheza kwa sababu walihisi njaa na kudhoofika.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_7', 3, NULL, 'assets/topic/asb/27234.png', 'Chifu wao kijana alitoka nje ya chumba na kuwasihi, "Acheni! Watu wapendwa wa kijiji cha Raha. Tafadhali acheni kulia. Tutaweza kufa hivi karibuni. Heri tutumie nguvu tulizo nazo kubaki hai."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_8', 3, NULL, 'assets/topic/asb/27235.png', 'Watu walipiga kelele, "Wewe ni chifu wetu! Lazima utuokoe!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_9', 3, NULL, 'assets/topic/asb/27236.png', 'Kwa hivyo, chifu Oza aliingia chumbani kwake akakusanya virago vyake na kuondoka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_10', 3, NULL, 'assets/topic/asb/27237.png', 'Halafu sauti ndogo na nyororo ikasema, "Nitakuwa chifu wenu!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_11', 3, NULL, 'assets/topic/asb/27238.png', 'Watu hao walishangaa kumwona mtoto mdogo mbele yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_12', 3, NULL, 'assets/topic/asb/27239.png', 'Watu hao walivutiwa sana na ujasiri wa mtoto huyo. Wakaahidi kumtii.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_13', 3, NULL, 'assets/topic/asb/27240.png', 'Wakisaidiwa na yule mvulana mdogo kama chifu wao, watu wa kijijini Raha, walianza kujifunza maana ya jitihada. Walifanya kazi kwa bidii.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24746_14', 3, NULL, 'assets/topic/asb/27241.png', 'Kila usiku, yule chifu mdogo aliondoka kijijini na kukutana na mzee mwenye busara.
