INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814', 2, 'What is wrong? / Kuna shida gani?', 'assets/topic/asb/10483.png', 'Ursula Nafula', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_3', 3, NULL, 'assets/topic/asb/10477.png', 'This is Matende.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_4', 3, NULL, 'assets/topic/asb/10478.png', 'Matende cries all the time.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_5', 3, NULL, 'assets/topic/asb/10479.png', 'When his mother feeds him, Matende cries.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_6', 3, NULL, 'assets/topic/asb/10480.png', 'When Matende is put down to rest, he still cries.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_7', 3, NULL, 'assets/topic/asb/10481.png', 'When his mother changes his nappies, Matende continues to cry.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_8', 3, NULL, 'assets/topic/asb/10482.png', 'When his mother carries him on her shoulder, Matende cries more.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_9', 3, NULL, 'assets/topic/asb/10483.png', 'When he falls asleep, Matende looks as if he is about to cry.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14814_10', 3, NULL, 'assets/topic/asb/10484.png', 'Matende''s mother looks at her son. She does not know what is wrong with him.
