INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111', 2, 'Zawadi ya Sisanda', 'assets/topic/asb/17355.png', 'Ursula Nafula', 'FF9DEDE3');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_3', 3, NULL, 'assets/topic/asb/17354.png', 'Sisanda ni msichana wa umri wa miaka minane. Kila siku anapotoka shule, anavua sare, anakula chakula cha mchana kisha yeye na babu yake wanacheza mchezo waupendao. Wanafurahia sana kucheza hadi Sisanda hataki kuacha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_4', 3, NULL, NULL, 'Babu anamwuliza kwa utani, "Utawezaje kuwa meneja wa benki usipokwenda shule ya upili?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_5', 3, NULL, NULL, 'Sisanda ni mrefu kwa umri wake. Anafuata  urefu wa babake. Anamfanana mamake kwa uso wake ulio mfiringo na kwa tabasamu yake. Kila siku wazazi wake huamka mapema na kwenda wanakofanya kazi kwenye hifadhi ya wanyama iliyo karibu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_6', 3, NULL, 'assets/topic/asb/17355.png', 'Maadhimisho ya kuzaliwa kwake iliyopita, Sisanda aliandaliwa sherehe maalum. Wazazi walipata ruhusa ya kuandaa sherehe hiyo katika hifadhi ya wanyama. Twiga walivutiwa sana na watu waliofika kwa sherehe hiyo. Waliyanyoosha mashingo yao marefu wakaiona sherehe vizuri na kutaka kuptata sehemu ya keki! Sisanda aliwapenda twiga zaidi ya wanyama wote kwa ajili ya upole na utulivu wao. Angewatazama kwa siku nzima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_7', 3, NULL, NULL, 'Ijumaa moja, babake Sisanda alikuja nyumbani mapema kutoka kazini. Alionekana kuwa mwenye hasira. Sisanda alimwuliza, "Baba, kuna nini kibaya?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_8', 3, NULL, 'assets/topic/asb/17356.png', '"Ah, la!" Sisanda alianza kulia. "Heri ningeweza kumsaidia. Labda yule ndama vilevile analia kama mimi." Sisanda alilia kwa muda mrefu. Mamake alijaribu kumfariji. Alimsomea hadithi moja zaidi wakati wa kulala ili aweze kusahau angalau kidogo uchungu aliokuwa nao kumhusu yule ndama. Hatimaye, Sisanda alipatwa na usingizi huku akisikiliza sauti ya mamake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_9', 3, NULL, NULL, 'Asubuhi iliyofuata Sisanda alikuwa na wazo! Alimwuliza babake, "Ninaweza kwenda kufanya kazi nawe leo? Ningependa kumpelekea mtoto twiga zawadi."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_10', 3, NULL, NULL, 'Siku hiyo ilikuwa yenye joto na mawingu. Kila kitu katika hifadhi ya wanyama kilitulia. Sisanda alisema, "Nadhani hakuna jua leo kwa sababu ya huzuni wa mtoto twiga."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_11', 3, NULL, 'assets/topic/asb/17357.png', 'Walimpata mtoto twiga akiwa peke yake. Alilegeza shingo lake refu na kuinama. Macho yake makubwa yalipumbaa. Sisanda alisimama aliufungua mkoba wake akatoa kitabu. Wazazi wake walishangaa alipoanza kumsomea mtoto twiga. Mtoto twiga aligeuza kichwa chake akatazama kulikotoka sauti ya Sisanda akasikiliza kama aliyelewa kila neno. Mwanzoni, wazazi walidhani kumsomea twiga ni jambo la ajabu, lakini walibadili mawazo yao walipomwona mtoto twiga akitulia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_12', 3, NULL, NULL, '"Hadithi yangu ilimfariji," Sisanda alimwambia babu yake alipofika nyumbani. Sisanda alienda kumtembelea mtoto twiga kila alasiri na wakati wa wikendi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_13', 3, NULL, NULL, 'Polepole, mtoto twiga alianza kupata nguvu. Watu waliofanya kazi katika hafidhi hiyo walimlinda vizuri. Upendo kutoka kwa rafiki yake Sisanda, ulifanya miujiza.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_14', 3, NULL, NULL, 'Siku iliyofuata meneja wa hifadhi alimpigia simu mwalimu wa Sisanda. Akawaalika wanafunzi wa darasa la Sisanda kumtembelea Furaha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_15', 3, NULL, 'assets/topic/asb/17358.png', 'Siku ya matembezi, wanafunzi 40 wa darasa la 3 walisubiri kwa hamu kufunguliwa kwa lango la hifadhi. Sisanda aliwaongoza wote wakaenda kumwona Furaha. Wengine walimtazama yule twiga mrefu kwa mshangao. Wengine walicheka kwa woga. Mwalimu wao, Bi Keziah, alitabasamu tu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_16', 3, NULL, NULL, '"Sisanda, rafiki yako ni mrembo. Umemsaidia sana," mwalimu alisema. "Anaitwaje?" Mvulana mmoja aliuliza. "Anaitwa Furaha," Sisanda alimjibu. Bi Keziah alisema, "Bila shaka anafurahi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_17', 3, NULL, NULL, 'Watoto waliketi chini wakamsikiliza Sisanda akisoma hadithi aliyomsomea Furaha siku aliyomtembelea mara ya kwanza. Meneja wa hifadhi alipiga picha. Baadhi ya watalii waliopita pia walipiga picha.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22111_18', 3, NULL, NULL, 'Ilikuwa zawadi ya kipekee! Sisanda kusoma hadithi ili kumponya rafiki.', NULL);
